import { initialState } from '../store'

export const gameReducer = (state = initialState.games, action) => {
    switch (action.type) {
        case 'GET_GAME_IMAGE':
            return {
                ...state,
                selected_card_images: [...state.selected_card_images, action.payload]
            }
        default:
            return state
    }
}
