import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getMusicSearch } from '../../redux/actions'
import { Card, Button, Spinner } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import ReactPlayer from 'react-player'





const MusicList = () => {

    const dispatch = useDispatch()
    const musicList = useSelector(state => state.music.music_lists)
    console.log(musicList)

    const loading = useSelector(state => state.music.loading)

    return (
        <div className="" >
            {
                loading
                    ? (<Spinner animation="border" variant="success" />)
                    : musicList[0]?.data.data.map((music) =>
                    (<>
                        {/* // <Link to={`/music/${music.id}/`} className="no-underline"> */}

                        <div className='mb-0 mt-0 flex'> <ReactPlayer url={music.preview}
                            // playing
                            controls={true}
                            // width={200}
                            pip={true}
                            stopOnUnmount={false}
                            loop={true}
                        />
                            <div className='flex align-items-center'>

                                <div>
                                    <img className='w-10 h-10 flex' src={music.album.cover_medium} alt="" />  </div>
                                <div className='text-gray-300 flex'> <span>{music.title_short}</span></div>
                            </div>
                        </div>

                        {/* // </Link> */}
                    </>)
                    )
            }
        </div>
    )
}

export default MusicList
