import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getMusicSearch } from '../../redux/actions'
import { Card, Button, Spinner, Container, Row, Col } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import ReactPlayer from 'react-player'





const MusicList = () => {

    const dispatch = useDispatch()
    const musicList = useSelector(state => state.music.music_lists)
    console.log(musicList)

    const loading = useSelector(state => state.music.loading)

    return (
        <Container className="" >
            {
                loading
                    ? (<Spinner animation="border" variant="success" />)
                    : musicList[0]?.data.data.map((music) =>
                    (<Row >
                        {/* // <Link to={`/music/${music.id}/`} className="no-underline"> */}

                        <Col md={6}><div className='flex flex-wrap'> <ReactPlayer url={music.preview}
                            // playing
                            controls={true}
                            width={200}
                            pip={true}
                            stopOnUnmount={false}
                            loop={true}
                            className='align-self-center'
                        />
                            <img className='w-10 h-10 align-self-center' src={music.album.cover_medium} alt="" />
                            <div className='text-gray-300 align-self-center'> <span>{music.title_short}</span></div>
                        </div>
                        </Col>

                        <Col md={3}>
                            {/* <div> */}
                            {/* <img className='w-10 h-10 flex' src={music.album.cover_medium} alt="" />  </div> */}
                        </Col>
                        <Col md={3}>

                        </Col>


                        {/* // </Link> */}
                    </Row>)
                    )
            }
        </Container>
    )
}

export default MusicList
