import React, { useEffect, useState } from 'react'
import ReactPlayer from 'react-player'
import { useSelector, useDispatch } from 'react-redux'
import ReactAudioPlayer from 'react-audio-player';
import { CgCloseR } from "react-icons/cg";
import Moveable from "react-moveable";
import { Rnd } from 'react-rnd';



const Player = () => {

    const [closePlayer, setClosePlayer] = useState(true)
    const [frame, setFrame] = useState({})

    const currentSong = useSelector(state => state.music.current_song)
    console.log(currentSong)



    useEffect(() => {
        setFrame({
            translate: [0, 0],
            rotate: 0
        })
        handleClosePlayer()
    }, [])

    const handleClosePlayer = () => {

        return setClosePlayer(false)
    }



    return (
        <div className={''}>

            {/* <Moveable
                target={document.querySelector(".target")}
                draggable={true}
                throttleDrag={0}
                onDragStart={({ set }) => {
                    set(frame.translate);
                }}
                onDrag={({ target, beforeTranslate }) => {
                    frame.translate = beforeTranslate;
                    target.style.transform
                        = `translate(${beforeTranslate[0]}px, ${beforeTranslate[1]}px)`;
                }}
                onDragEnd={({ target, isDrag, clientX, clientY }) => {
                    console.log("onDragEnd", target, isDrag);
                }}
            /> */}


            <div
                className='fixed-bottom h-20 w-20'
            >
                <Rnd
                    default={{
                        x: 10,
                        y: 10,
                        width: 900,
                        height: 900,
                    }}
                    minWidth={500}
                    minHeight={0}
                    bounds="window"
                >
                    <div className={currentSong === null ? 'hidden' : 'resize target rotat px-5 mx-5  flex justify-between opacity-80 bg-pink-800 align-items-center py-1 rounded-full'} >

                        <div className={currentSong === null ? 'hidden' : 'flex align-items-center'}>
                            <img className="h-10 w-10 rounded-full mx-2" src={currentSong?.artist.picture} alt="" />
                            <div className='flex'>
                                <h6 className=' text-gray-200 mx-4'>{currentSong?.title_short}</h6>
                                <span className='mx-auto text-sm text-gray-200'><i>- {currentSong?.artist.name}</i></span>
                            </div>
                        </div>
                        <div className={currentSong === null ? 'hidden' : 'mx-2'}>

                            <ReactAudioPlayer
                                src={currentSong?.preview}
                                // autoPlay
                                controls
                                className="mx-auto text-blue-600"
                                loop={true}
                            />
                        </div>

                        <div className={'text-gray-200'}>

                            <button onclick={() => handleClosePlayer()}><CgCloseR /></button>
                        </div>
                    </div>

                </Rnd>
            </div>

        </div>
    )
}

export default Player
