import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getMovieCategory } from '../../redux/actions'
import { Card, Button, Modal } from 'react-bootstrap'


const UpComingMovies = () => {


    const dispatch = useDispatch()
    const upComingMovies = useSelector(state => state.movies.movie_category)
    console.log(upComingMovies[0])

    useEffect(() => {
        dispatch(getMovieCategory())
    }, [])

    const IMG_URL = 'https://image.tmdb.org/t/p/original'




    return (
        <div className=' text-gray-300 justify-center'>
            <h6 className='text-center'>Upcoming Movies</h6>
            <div className=' justify-center md:flex flex-col'>
                <Card className='w-20 h-20 mx-1 mb-5 mt-3 flex   hover:border-purple-800 hover:text-gray-200 border-blue-600 border-b-2 hover:bg-blue-800 rounded-full' >
                    {
                        upComingMovies[0].data.results?.map((movie) =>
                        (<div key={movie.id} className=''>

                            <Card.Img variant="top" className="img-fluid h-5 w-5" src={IMG_URL + movie.poster_path} />

                            <i className='text-gray-400 text-xs'>{movie.release_date}</i>



                        </div>)
                        ).reverse()
                    }
                </Card>
            </div>
        </div>
    )
}

export default UpComingMovies
