import React, { useEffect, useState } from 'react'
import { Form } from 'react-bootstrap'
import { fetchAllCryptoMarket } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'


const CryptoSearch = () => {
    const [query, setQuery] = useState('')

    const dispatch = useDispatch()
    const allCryptoMarket = useSelector(state => state.crypto.all_crypto_market)

    console.log(allCryptoMarket)

    useEffect((e) => {

    }, [])


    const handleSearch = (qry) => {
        let searchQuery = allCryptoMarket.find(crypto => crypto.name === query)
        console.log(searchQuery)
        if (searchQuery) {
            dispatch(fetchAllCryptoMarket([searchQuery]))
        }

    }


    return (
        <div className='sticky top-40 z-40 bg-gray-900 focus:ring-2 focus:ring-blue-600'>
            <Form>
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                    <Form.Control type="search" placeholder="search crypto name"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        onKeyUp={() => handleSearch()}
                    />
                </Form.Group>

            </Form>
        </div>
    )
}

export default CryptoSearch
