import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage, imageVisibility } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'


const MemoryGame = () => {

    const [allImages, setAllImages] = useState(null)
    const [target, setTarget] = useState(null)
    const [visible, setVisible] = useState(false)

    const dispatch = useDispatch()
    const selectedImage = useSelector(state => state.games.selected_card_images)
    const visibleImage = useSelector(state => state.games.visible)
    console.log(selectedImage)

    const imagesData = cardImages

    const handLoadImage = () => {
        return imagesData?.images?.sort((a, b) => 0.5 - Math.random())
    }

    useEffect(() => {
        handLoadImage()
        dispatch(imageVisibility())
    }, [])

    // console.log(imagesData)


    const handleImageClick = (e, img) => {
        // (selectedImage.filter(image => image.id === img.id) ? setVisible(!visibleImage) : setVisible(visibleImage))

        setVisible(!visible)
        // console.log(target, 'click')
        // }
        // dispatch(selectedCardImage(img))
    }

    // w-20 h-20 opacity-100' : ' w-20 h-20 opacity-0

    return (
        <>
            <div className="flex flex-wrap bg-gray-50">
                {
                    imagesData.images.map(img => (
                        <>
                            <div className={' border rounded-lg border-3 '}>
                                <img className={'h-20 w-20'} src={img.image} alt="" />
                            </div>

                        </>
                    ))
                }

            </div>

        </>

    )
}

export default MemoryGame
