import React, { useEffect, useState } from 'react'
import { Card, Button } from 'react-bootstrap'
import { useSelector, useDispatch } from 'react-redux'
import { fetchMovies } from '../redux/actions'


const MovieList = () => {


    const allMovies = useSelector(state => state.movies.movies)
    console.log(allMovies.data)

    const IMG_URL = 'https://image.tmdb.org/t/p/original'

    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(fetchMovies())
    }, [])

    return (
        <>
            <div className="flex flex-wrap justify-center" >
                {
                    allMovies.data.results?.map((movie) =>
                    (<Card key={movie.id} className='w-40 h-40 mx-4 my-2  border-purple-800  border-2' >
                        <Card.Img variant="top" className="img-fluid" src={IMG_URL + movie.backdrop_path} />
                        <Card.Body>
                            <span>{movie.original_title}</span>
                            <Card.Text>
                                Nice holiday
                            </Card.Text>
                            {/* <Button variant="primary">View</Button> */}
                        </Card.Body>
                    </Card>)
                    )
                }
            </div>

        </>
    )
}

export default MovieList
