import React, { useState, useEffect } from 'react'
import { FiPlusCircle } from "react-icons/fi";
import { useSelector, useDispatch } from 'react-redux'
import { fetchUsers } from '../../redux/actions'


const FollowNewUsers = () => {

    const dispatch = useDispatch()

    const allUsers = useSelector(state => state.users.users)
    console.log(allUsers)


    const totalUsers = () => {
        return allUsers.length
    }


    useEffect(() => {
        dispatch(fetchUsers())
        totalUsers()
    }, [totalUsers()])

    return (
        <div className='  flex flex-wrap mb-5 p-2 gap-4 text-sm bg-gray-900 w-100 h-100  flex-col '>
            <div className='text-light md:text-xs flex justify-center align-items-center'><div className=' text-lg'><FiPlusCircle /></div><span className='mx-2 text-lg'>Follow New User</span></div>
            {
                allUsers?.map(user => (
                    <>
                        <div className=' md:text-xs md:flex-col relative mx-3 '>
                            <div className='absolute -inset-0.5 opacity-50 bg-blue-600 rounded-lg blur-xl  '></div>
                            <div className='bg-gray-900 rounded-lg  border-1 border-blue-600  text-center text-gray-300 leading-none p-2 relative flex align-items-center'><img className='h-5 w-5 rounded-full mx-1 flex flex-start cursor-pointer' src={user.image} alt="" /> <span className='ml-1  flex flex-initial'>{user.firstName}</span> <span className="text-xl flex flex-end cursor-pointer lg:mx-auto"><FiPlusCircle /></span></div> </div>


                    </>))
            }
        </div>
    )
}

export default FollowNewUsers
