import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getMusicSearch } from '../../redux/actions'
import { Card, Button, Spinner } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import ReactPlayer from 'react-player'





const MusicList = () => {

    const dispatch = useDispatch()
    const musicList = useSelector(state => state.music.music_lists)
    console.log(musicList)

    const loading = useSelector(state => state.music.loading)

    return (
        <div className="flex flex-wrap flex-col justify-center pb-5" >
            {
                loading
                    ? (<Spinner animation="border" variant="success" />)
                    : musicList[0]?.data.data.map((music) =>
                    (<>
                        {/* // <Link to={`/music/${music.id}/`} className="no-underline"> */}

                        <img src={music.album.cover_medium} alt="" />
                        {music.title_short}
                        <ReactPlayer url={music.preview}
                            // playing
                            controls={true}
                            width={300}
                            pip={true}
                            stopOnUnmount={false}
                            loop={true}
                        />

                        {/* // </Link> */}
                    </>)
                    )
            }
        </div>
    )
}

export default MusicList
