import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getMusicSearch } from '../../redux/actions'
import { Card, Button, Spinner, Container, Row, Col } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import ReactPlayer from 'react-player'
import { ImPlay2 } from "react-icons/im";







const MusicList = () => {

    const dispatch = useDispatch()
    const musicList = useSelector(state => state.music.music_lists)
    console.log(musicList)

    const loading = useSelector(state => state.music.loading)


    const style = {
        BackgroundColor: 'green',
        marginTop: '0',
        marginBottom: '0px'
    }

    return (
        <div className="" >
            {
                loading
                    ? (<Spinner animation="border" variant="success" />)
                    : musicList[0]?.data.data.map((music, i) =>
                    (<div className=" flex border-b my-4 border-gray-700 align-items-center">
                        {/* // <Link to={`/music/${music.id}/`} className="no-underline"> */}


                        <div> <img className='w-10 h-10 ' src={music.album.cover_medium} alt="" /></div>
                        <div><span className='text-gray-300 '>{music.title_short}</span></div>




                        {/* // </Link> */}
                    </div>)
                    )
            }
        </div>
    )
}

export default MusicList
