import { initialState } from '../store/'



export const postsReducer = (state = initialState.posts, action) => {
    switch (action.type) {
        case 'FETCH_POSTS':
            return {
                ...state,
                posts: action.payload,
            }

        case 'ADD_POST':
            return {
                ...state,
                posts: [...state.posts, action.payload],
            }

        case 'ADD_LIKES':
            return {
                ...state,
                likes: [...state.likes, action.payload],
            }

        case 'REMOVE_LIKES':
            return {
                ...state,
                likes: [...state.likes].filter(p => p.id !== action.payload.id)
            }

        default:
            return state

    }
}
