import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { fetchUsers } from '../redux/actions'
import { Form, Button, Alert } from 'react-bootstrap'
import { withRouter, Link } from 'react-router-dom'

const Login = ({ history }) => {
    const [useremail, setUseremail] = useState('')
    const [userpassword, setUserpassword] = useState('')
    const [loggedin, setLoggedin] = useState(false)


    const allUsers = useSelector(state => state.users.users)
    console.log(allUsers)

    const getUsersEmail = allUsers?.map((user) => user.email)
    const getUsersPassword = allUsers?.map((user) => (user.password))
    const usersEmailsAndPasswords = getUsersEmail?.concat(getUsersPassword)

    console.log(usersEmailsAndPasswords)


    const handleEmailInputChange = (e) => {
        setUseremail(e.target.value)
    }

    const handlePasswordInputChange = (e) => {
        setUserpassword(e.target.value)
    }


    const confirmLoginDetails = () => {
        if (usersEmailsAndPasswords.includes(useremail && userpassword)) {
            return history.push('/')
        }

    }


    return (
        <div className='w-50 mx-auto pt-20'>
            <Form className='mb-10'>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="Enter email"
                        value={useremail}
                        onChange={(e) => handleEmailInputChange(e)}
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password"
                        value={userpassword}
                        onChange={(e) => handlePasswordInputChange(e)}

                    />
                </Form.Group>

                <button onClick={() => confirmLoginDetails()} type="submit" className="mx-3 mt-1 text-center text-light text-xl p-2 flex border-green-500 border-b-2 border-t-2 hover:border-green-900 rounded-full ">
                    Login
                </button>

            </Form>
            <div className="text-grey-100">Don't have an account?
                <Link to="/signup">Sign Up</Link> </div>
        </div>
    )
}

export default withRouter(Login)
