import { initialState } from '../store/'



export const postsReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'FETCH_POSTS':
            return {
                ...state,
                posts: action.payload,
            }

        case 'ADD_POST':
            return {
                ...state,
                posts: [...posts, action.payload]
            }
        default:
            return state

    }
}
