import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { fetchAllCryptoMarket } from '../../redux/actions'
import { Card, Button, Spinner, Container, Row, Col, Modal } from 'react-bootstrap'


const AllCryptoMarket = () => {

    const dispatch = useDispatch()

    const allCryptoMarket = useSelector(state => state.crypto.all_crypto_market)

    const cryptoPriceChange = allCryptoMarket.map(cryptoprice => cryptoprice.current_price)

    console.log(allCryptoMarket)

    useEffect(() => {
        dispatch(fetchAllCryptoMarket())
    }, [cryptoPriceChange])


    const symbolToUpperCase = (symbol) => symbol.toUpperCase()

    return (
        <div className='text-gray-300   '>
            <Container fluid className='overflow-x-auto'>
                <div className='border-b border-gray-500 p-1'>
                    <div className='flex justify-center  mt-5 mb-2 '>

                        <h3 className='text-center text-gray-300 mx-1'>Crypto Market  </h3>
                        <span className="text-yellow-500">Live Update</span>
                    </div>
                    <Row className='mt-5 align-items-center'>

                        <Col md={1} className="text-center"> #</Col>
                        {/* <Col md={1}>Name</Col> */}

                        <Col md={3} className="text-center" > Name</Col>

                        <Col md={2}>Price </Col>


                        <Col md={2} className="text-center">lowest in 24h</Col>

                        <Col md={2}>Market Cap</Col>

                        <Col md={2}>Circulating Supply</Col>

                    </Row>
                </div>
                {
                    allCryptoMarket.map(crypto => (

                        <Row className='my-5 align-items-center hover:bg-blue-700 hover:text-gray-200 rounded-lg p-1'>

                            <Col md={1}> {crypto.market_cap_rank}</Col>
                            {/* <Col md={1}> </Col> */}

                            <Col md={3}  ><div className="flex justify-evenly align-items-center"><div><img className="w-20 h-20" src={crypto.image} alt="" /> </div><div>{crypto.name} </div><span className='text-gray-500'>{symbolToUpperCase(crypto.symbol)}</span></div></Col>

                            <Col md={2}>${crypto.current_price}</Col>

                            <Col md={2}>{crypto.low_24h}</Col>

                            <Col md={2}>${crypto.market_cap}</Col>

                            <Col md={2}>{crypto.circulating_supply}</Col>

                        </Row>
                    ))
                }
            </Container>
        </div>
    )
}

export default AllCryptoMarket
