import React, { useEffect, useState } from 'react'
import { Card, Button, Modal } from 'react-bootstrap'
import { useSelector, useDispatch } from 'react-redux'
import { fetchMovies } from '../redux/actions'
import MovieModal from './MovieModal'


const MovieList = () => {



    const [modalShow, setModalShow] = React.useState(false);

    const dispatch = useDispatch()
    const allMovies = useSelector(state => state.movies.movies)
    console.log(allMovies.data)
    const IMG_URL = 'https://image.tmdb.org/t/p/original'



    const shortTitle = (title) => {
        return title.slice(0, 20)
    }


    useEffect(() => {
        dispatch(fetchMovies())
    }, [])







    const MyVerticallyCenteredModal = ({ onHide }) => {
        return (
            <Modal

                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Modal heading
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h4>Centered Modal</h4>
                    <p>
                        Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
                        dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
                        consectetur ac, vestibulum at eros.
                    </p>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        );
    }



    return (
        <>
            <div className="flex flex-wrap justify-center" >
                {
                    allMovies.data.results?.slice(0, 200).map((movie) =>
                    (<Card onClick={() => setModalShow(true)} key={movie.id} className='w-40 h-40 mx-4 my-2  hover:border-purple-800 hover:text-gray-200 border-blue-600 border-b-2 hover:bg-blue-800 rounded-full' >
                        <Card.Img variant="top" className="img-fluid" src={IMG_URL + movie.backdrop_path} />
                        <Card.Body>
                            <span >{shortTitle(movie.title)}</span>
                            <Card.Text>

                            </Card.Text>

                        </Card.Body>
                    </Card>)
                    )
                }
            </div>

            <MyVerticallyCenteredModal
                show={modalShow}
                onHide={() => setModalShow(false)}
            />
        </>
    )
}

export default MovieList
