import React, { useState, useEffect } from 'react'
import allCardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage, imageVisibility, memoryGameTotalScore } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'
import { shuffle } from 'lodash'
import { ImImages } from 'react-icons/im'
import './MemoryGame.css'
import { Alert } from 'react-bootstrap'


const MemoryGame = () => {
    const [cardImages, setCardImages] = useState([])
    const [openedCard, setOpenedCard] = useState([]);
    const [matched, setMatched] = useState([]);
    const [win, setWin] = useState(false);

    const dispatch = useDispatch()

    useEffect(() => {
        shuffle(cardImages)
    }, [cardImages.length])

    function flipCard(index) {
        setOpenedCard((opened) => [...opened, index]);
    }

    const handLoadImage = () => {
        setCardImages([...allCardImages?.images, ...allCardImages?.images])
        dispatch(memoryGameTotalScore([]))

    }

    const handleGameReset = () => {
        setCardImages([])
        setOpenedCard([])
        setMatched([])
        dispatch(memoryGameTotalScore([]))

    }

    let counter = 0
    const gameScore = matched?.map((matched, index) => index + 1 ? counter + 5 : counter)

    console.log(matched.length)

    console.log(gameScore)

    useEffect(() => {


        if (openedCard < 2) return;

        const firstMatched = cardImages[openedCard[0]];
        const secondMatched = cardImages[openedCard[1]];

        if (secondMatched && firstMatched.id === secondMatched.id) {
            setMatched([...matched, firstMatched.id]);
        }

        if (openedCard.length === 2) setTimeout(() => setOpenedCard([]), 1000);


        dispatch(memoryGameTotalScore(gameScore))



    }, [openedCard]);




    setTimeout(() => {
        return matched.length - 1 ? setWin(true) : ''

    }, 3000)
    return (
        <>
            {



                win ? <Alert variant="success" className="text-center">
                    <Alert.Heading>Congratulations! You WON!!!</Alert.Heading>
                    <p>
                        You have a good memory IQ.
                    </p>
                    <hr />
                    <p className="mb-0">
                        Play again!
                    </p>
                </Alert>
                    : ''


            }
            <div className="flex flex-wrap">


                {cardImages?.map((img, index) => {

                    let isFlipped = false;

                    if (openedCard.includes(index)) isFlipped = true;
                    if (matched.includes(img.id)) isFlipped = true;
                    return (
                        <div
                            className={`image-card m-1 border-b border-t-2 border-r-2 border-l-2 border-pink-400 ${isFlipped ? "flipped" : ""} `}
                            key={index}
                            onClick={() => flipCard(index)}
                        >
                            <div className="inner">
                                <div className="front">
                                    <img
                                        src={img.image}
                                        alt=""
                                        className='w-100 h-100 rounded-lg'
                                    />
                                </div>
                                <div className="back bg-indigo-900 hover:bg-indigo-400"></div>
                            </div>
                        </div>
                    );
                })}
            </div>


            <div className='text-center'>
                {cardImages.length === 0
                    ?
                    <button onClick={() => handLoadImage()} className="text-gray-200 bg-indigo-900 text-xl my-5 p-2 rounded-lg"><pre>START GAME</pre></button>
                    :
                    <button onClick={() => handleGameReset()} className="text-gray-200 bg-red-600 text-xl my-2 p-2 rounded-lg"><pre>END GAME</pre></button>
                }
            </div>
        </>
    );
}




export default MemoryGame
