import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getMusicSearch } from '../../redux/actions'
import { Card, Button, Spinner, Container, Row, Col } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import ReactPlayer from 'react-player'





const MusicList = () => {

    const dispatch = useDispatch()
    const musicList = useSelector(state => state.music.music_lists)
    console.log(musicList)

    const loading = useSelector(state => state.music.loading)


    const style = {
        BackgroundColor: 'green',
        marginTop: '0',
        marginBottom: '0px'
    }

    return (
        <Container className="" >
            {
                loading
                    ? (<Spinner animation="border" variant="success" />)
                    : musicList[0]?.data.data.map((music) =>
                    (<Row >
                        {/* // <Link to={`/music/${music.id}/`} className="no-underline"> */}

                        <Col md={12}>
                            <ReactPlayer url={music.preview}
                                controls={true}
                                // width={ }
                                pip={true}
                                stopOnUnmount={false}
                                loop={true}
                                style={style}
                            />
                            <img className='w-10 h-10 ' src={music.album.cover_medium} alt="" />
                            <span className='text-gray-300 '>{music.title_short}</span>

                        </Col>


                        {/* // </Link> */}
                    </Row>)
                    )
            }
        </Container>
    )
}

export default MusicList
