import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'


const MemoryGame = () => {

    const [allImages, setAllImages] = useState(null)
    // const[imageIndexArray, setImageIndexArray] = useState([])



    const imageIndex = allImages?.images?.map(image => image.id)
    // setImageIndexArray(imageIndex)
    console.log(imageIndex)


    const shuffleArray = (arr) => {
        for (let i = arr?.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            const temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
    }

    useEffect(() => {
        setAllImages(cardImages)
        shuffleArray(imageIndex)
    }, [imageIndex])

    console.log(allImages)

    return (
        <div className="flex flex-wrap">
            {
                allImages?.images?.sort((a, b) => 0.5 - Math.random()).map((image) => (
                    <>
                        <div className='flex border rounded-lg'>

                            <img className='w-20 h-20' src={image.image} alt="" />


                        </div>
                    </>))
            }
        </div>


    )
}

export default MemoryGame
