import React, { useEffect, useState } from 'react'
import { Form } from 'react-bootstrap'
import { fetchSearchedCrypto } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'


const CryptoSearch = () => {
    const [query, setQuery] = useState('')
    const [searchCrypto, setSearchCrypto] = useState({})

    const dispatch = useDispatch()
    const allCryptoMarket = useSelector(state => state.crypto.all_crypto_market)

    // console.log()

    useEffect((e) => {
        handleSearch()

    }, [query])

    console.log(query)


    const handleSearch = () => {

        return allCryptoMarket.find(crypto => crypto.name.toLowerCase() === query.toLowerCase() ? (setSearchCrypto([crypto]), dispatch(fetchSearchedCrypto(searchCrypto))) : dispatch(fetchSearchedCrypto({})))
    }

    console.log(searchCrypto)

    return (
        <div className='sticky top-40 z-40 bg-gray-900 focus:ring-2 focus:ring-blue-600'>
            <Form>
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                    <Form.Control type="search" placeholder="search crypto name"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        onKeyUp={() => handleSearch()}
                    />
                </Form.Group>

            </Form>
        </div>
    )
}

export default CryptoSearch
