import React, { useEffect } from 'react'
import { Form } from 'react-bootstrap'
import { fetchAllCryptoMarket } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'


const CryptoSearch = () => {

    const dispatch = useDispatch()
    const searchQuery = useSelector(state => state.crypto.all_crypto_market)

    console.log(searchQuery)

    useEffect((e) => {
        dispatch(fetchAllCryptoMarket(e))
    }, [])


    return (
        <div className='sticky top-40 z-40 bg-gray-900 focus:ring-2 focus:ring-blue-600'>
            <Form>
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                    <Form.Control type="search" placeholder="search crypto "
                        onChange={(e) => dispatch(fetchAllCryptoMarket(e.target.value))} />
                </Form.Group>

            </Form>
        </div>
    )
}

export default CryptoSearch
