import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'


const MemoryGame = () => {

    const [allImages, setAllImages] = useState(null)
    const [target, setTarget] = useState(null)
    const [visible, setVisible] = useState(false)

    const dispatch = useDispatch()

    useEffect(() => {
        setAllImages(cardImages)
    }, [])

    const handleImageClick = (e, image) => {
        // setTarget(e)
        // setVisible(!visible)

        dispatch(selectedCardImage(image))

        // console.log('Image clicked', image.name)
    }

    // console.log(allImages)

    return (
        <>
            <div className="flex flex-wrap">
                {
                    allImages?.images?.sort((a, b) => 0.5 - Math.random()).map((image) => (
                        <div className='flex border rounded-lg '>

                            <img onClick={(e) => handleImageClick(e.target, image)} className={target && visible ? ' w-20 h-20 opacity-100' : ' w-20 h-20 opacity-0'} src={image.image} alt="" />


                        </div>
                    ))
                }
            </div>


        </>
    )
}

export default MemoryGame
