import React from 'react'
import Player from '../Music/Player'
import MyNavBar from './MyNavBar'
import SideBarRight from './SideBarRight'
import { useSelector, useDispatch } from 'react-redux'
import { Container, Row, Col, Card, ListGroupItem, ListGroup } from 'react-bootstrap'


const ProfilePage = () => {

    const currentUser = useSelector(state => state.users.getcurrentuser)






    return (
        <>
            <div className='mx-5'>

                {

                    (<Container fluid>
                        <Row className='sticky-top '>

                            <div className=" " >
                                <MyNavBar />
                            </div>
                        </Row>

                        <Row className=' justify-center '>
                            <Col md={3} className=' '>
                                <div className="sticky top-40 shadow-2xl flex flex-wrap mx-1 drop-shadow-2xl  rounded " >



                                </div>
                            </Col>

                            {currentUser.image}
                            {currentUser.firstName} {currentUser.lastName}
                            {currentUser.email}
                            {currentUser.city}
                            <Col md={6}>
                                <div className=" border-blue-600 drop-shadow-2xl border-b-2 rounded my-2 mx-2 shadow-2xl" >
                                    <h3 className='text-center text-gray-200'>User Profile</h3>


                                    {/* <Player /> */}
                                </div>
                            </Col>
                            <Col md={3}>
                                <div className=" sticky top-40 shadow-2xl flex flex-wrap mx-1 border-blue-900 drop-shadow-2xl border-b-2 rounded " >

                                    {/* <SideBarRight /> */}

                                </div>
                            </Col>
                            {/* </div> */}
                        </Row>

                    </Container>)}
            </div>

        </>
    )
}

export default ProfilePage
