import React, { useEffect, useState } from 'react'
import ReactPlayer from 'react-player'
import { useSelector, useDispatch } from 'react-redux'
import ReactAudioPlayer from 'react-audio-player';
import { CgCloseR } from "react-icons/cg";
import Moveable from "react-moveable";




const Player = () => {

    const [closePlayer, setClosePlayer] = useState(true)
    const [frame, setFrame] = useState({})

    const currentSong = useSelector(state => state.music.current_song)
    console.log(currentSong)

    // const frame = {
    //     translate: [0, 0],
    // };


    useEffect(() => {
        setFrame({
            translate: [0, 0],
            rotate: 0
        })
        handleClosePlayer()
    }, [])

    const handleClosePlayer = () => {

        return setClosePlayer(false)
    }



    return (
        <div className={''}>
            <div className={currentSong === null ? 'hidden' : 'resize target rotat px-5 mx-5 fixed-bottom flex justify-between opacity-80 bg-pink-800 align-items-center py-1 rounded-full'} >

                <div className={currentSong === null ? 'hidden' : 'flex align-items-center'}>
                    <img className="h-10 w-10 rounded-full mx-2" src={currentSong?.artist.picture} alt="" />
                    <div className='flex'>
                        <h6 className=' text-gray-200 mx-4'>{currentSong?.title_short}</h6>
                        <span className='mx-auto text-sm text-gray-200'><i>- {currentSong?.artist.name}</i></span>
                    </div>
                </div>
                <div className={currentSong === null ? 'hidden' : 'mx-2'}>

                    <ReactAudioPlayer
                        src={currentSong?.preview}
                        // autoPlay
                        controls
                        className="mx-auto text-blue-600"
                        loop={true}
                    />
                </div>

                <div className={'text-gray-200'}>

                    <button onclick={() => handleClosePlayer()}><CgCloseR /></button>
                </div>
            </div>

            <Moveable
                target={document.querySelector(".target")}
                draggable={true}
                throttleDrag={0}
                onDragStart={({ set }) => {
                    set(frame.translate);
                }}
                onDrag={({ target, beforeTranslate }) => {
                    frame.translate = beforeTranslate;
                    target.style.transform
                        = `translate(${beforeTranslate[0]}px, ${beforeTranslate[1]}px)`;
                }}
                onDragEnd={({ target, isDrag, clientX, clientY }) => {
                    console.log("onDragEnd", target, isDrag);
                }}
            />

            <Moveable
                target={document.querySelector(".rotat")}
                rotatable={true}
                throttleRotate={0}
                rotationPosition="top"
                onRotateStart={({ set }) => {
                    set(frame.rotate);
                }}
                onRotate={({ target, beforeRotate }) => {
                    frame.rotate = beforeRotate;
                    target.style.transform = `rotate(${beforeRotate}deg)`;
                }}
                onRotateEnd={({ target, isDrag, clientX, clientY }) => {
                    console.log("onRotateEnd", target, isDrag);
                }} />


            <Moveable
                target={document.querySelector(".resize")}
                resizable={true}
                throttleResize={0}
                keepRatio={false}
                onResizeStart={({ target, set, setOrigin, dragStart }) => {
                    // Set origin if transform-origin use %.
                    setOrigin(["%", "%"]);

                    // If cssSize and offsetSize are different, set cssSize. (no box-sizing)
                    const style = window.getComputedStyle(target);
                    const cssWidth = parseFloat(style.width);
                    const cssHeight = parseFloat(style.height);
                    set([cssWidth, cssHeight]);

                    // If a drag event has already occurred, there is no dragStart.
                    dragStart && dragStart.set(frame.translate);
                }}
                onResize={({ target, width, height, drag }) => {
                    target.style.width = `${width}px`;
                    target.style.height = `${height}px`;

                    // get drag event
                    frame.translate = drag.beforeTranslate;
                    target.style.transform
                        = `translate(${drag.beforeTranslate[0]}px, ${drag.beforeTranslate[1]}px)`;
                }}
                onResizeEnd={({ target, isDrag, clientX, clientY }) => {
                    console.log("onResizeEnd", target, isDrag);
                }} />
        </div>
    )
}

export default Player
