import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage, imageVisibility } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'
import { shuffle } from 'lodash'


const MemoryGame = () => {

    const [allImages, setAllImages] = useState([])
    const [selected, setSelected] = useState(null)
    const [visible, setVisible] = useState(false)

    const dispatch = useDispatch()
    const selectedImage = useSelector(state => state.games.selected_card_images)
    const visibleImage = useSelector(state => state.games.visible)
    console.log(allImages)

    const imagesData = cardImages




    const handleGameReset = () => {

        setAllImages([])
    }

    const handLoadImage = () => {
        setAllImages(imagesData)
        // return imagesData?.images?.sort((a, b) => 0.5 - Math.random())
    }

    useEffect(() => {
        // handLoadImage()
        dispatch(imageVisibility())
    }, [])

    // console.log(imagesData)


    const handleImageClick = (img, i) => {
        setSelected(i)
    }
    console.log(selected)

    // w-20 h-20 opacity-100' : ' w-20 h-20 opacity-0

    return (
        <div className="flex flex-col">
            <div className="flex flex-wrap ">
                {
                    allImages?.images?.map((img, i) => (
                        <>
                            <div onClick={() => handleImageClick(img, i)} className={' border rounded-lg border-3 '}>
                                <img className={'h-20 w-20'} src={img.image} alt="" />
                            </div>

                        </>
                    ))
                }

            </div>
            <div className='text-center'>
                {Object.keys(allImages).length === 0
                    ?
                    <button onClick={() => handLoadImage()} className="text-gray-200 bg-indigo-900 text-3xl p-2 rounded-lg">START</button>
                    :
                    <button onClick={() => handleGameReset()} className="text-gray-200 bg-red-900 text-3xl p-2 rounded-lg">End or Reset</button>
                }
            </div>
        </div>

    )
}

export default MemoryGame
