import React, { useEffect } from 'react'
import ReactPlayer from 'react-player'
import { useSelector, useDispatch } from 'react-redux'



const Player = () => {

    const currentSongMp3Link = useSelector(state => state.music.current_song_mp3_link)
    console.log(currentSongMp3Link)

    return (
        <div>
            <ReactPlayer url={currentSongMp3Link.preview}
                // playing
                controls={true}
                width={300}
                pip={true}
                stopOnUnmount={false}
                loop={true}
            />

        </div>
    )
}

export default Player
