import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage, imageVisibility } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'
import { shuffle } from 'lodash'


const MemoryGame = () => {

    const [allImages, setAllImages] = useState([])
    const [selected, setSelected] = useState(null)
    const [selectedImagesArray, setSelectedImagesArray] = useState([])
    const [imageUniqueName, setImageUniqueName] = useState([])

    const dispatch = useDispatch()
    const selectedImage = useSelector(state => state.games.selected_card_images)
    const visibleImage = useSelector(state => state.games.visible)



    console.log(selectedImagesArray)


    const handleGameReset = () => {

        setAllImages([])
    }


    const handLoadImage = () => {
        setAllImages(shuffle(cardImages.images))
    }

    useEffect(() => {
        // dispatch(imageVisibility())
    }, [])



    const imgNames = selectedImagesArray.map(image => image.name)


    const handleImageClick = (img, i) => {
        setSelected(i || img.id)
        setSelectedImagesArray([...selectedImagesArray, img])
        setImageUniqueName(imgNames)
    }
    console.log(selected)

    console.log(imageUniqueName)

    return (
        <div className="flex flex-col">
            <div className="flex flex-wrap ">
                {
                    allImages?.map((img, i) => (
                        <>
                            <div onClick={() => handleImageClick(img, i)} className={' border rounded-lg border-3 '}>
                                <img className={(selected === i || img.id) && imageUniqueName.filter(image => image === img.name) ? 'w-20 h-20 opacity-100' : ' w-20 h-20 opacity-0'} src={img.image} alt="" />
                            </div>

                        </>
                    ))
                }

            </div>
            <div className='text-center'>
                {Object.keys(allImages).length === 0
                    ?
                    <button onClick={() => handLoadImage()} className="text-gray-200 bg-indigo-900 text-3xl p-2 rounded-lg">START</button>
                    :
                    <button onClick={() => handleGameReset()} className="text-gray-200 bg-red-900 text-3xl p-2 rounded-lg">End or Reset</button>
                }
            </div>
        </div>

    )
}

export default MemoryGame
