import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage, imageVisibility } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'


const MemoryGame = () => {

    const [allImages, setAllImages] = useState(null)
    const [target, setTarget] = useState(null)


    const dispatch = useDispatch()
    const selectedImage = useSelector(state => state.games.selected_card_images)
    const visibleImage = useSelector(state => state.games.visible)
    console.log(visibleImage)

    const imagesData = cardImages

    const handLoadImage = () => {
        return imagesData?.images?.sort((a, b) => 0.5 - Math.random())
    }

    useEffect(() => {
        handLoadImage()
        dispatch(imageVisibility())
    }, [])

    // console.log(imagesData)


    const handleImageClick = (img) => {
        if (selectedImage.filter(image => image.id === img.id)) {
            // setVisible(!visible)
        }
        // console.log(target, 'click')
        // }
        dispatch(selectedCardImage(img))
    }



    return (
        <>
            <div className="flex flex-wrap">
                {
                    imagesData.images.map(img => (
                        <>

                            {/* visible ? ' w-20 h-20 opacity-100' :  */}
                            <div className='flex border rounded-lg '>
                                <img onClick={(e) => handleImageClick(img)} className={' w-20 h-20 opacity-100'} src={img.image} alt="" />
                            </div>





                        </>
                    ))
                }
            </div>


        </>

    )
}

export default MemoryGame
