import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage, imageVisibility } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'
import { shuffle } from 'lodash'
import { ImImages } from 'react-icons/im'
import './MemoryGame.css'


const MemoryGame = () => {

    const [openedCard, setOpenedCard] = useState([]);
    const [matched, setMatched] = useState([]);

    // const pokemons = [
    //     { id: 1, name: "balbasaur" },
    //     { id: 8, name: "wartotle" },
    //     { id: 9, name: "blastoise" },
    //     { id: 6, name: "charizard" }
    // ];

    //currently there are 4 pokemons but we need the pair

    const pairOfImages = [...cardImages, ...cardImages];

    function flipCard(index) {
        setOpenedCard((opened) => [...opened, index]);
    }

    useEffect(() => {
        if (openedCard < 2) return;

        const firstMatched = pairOfImages?.images[openedCard[0]];
        const secondMatched = pairOfImages?.images[openedCard[1]];

        if (secondMatched && firstMatched.name === secondMatched.name) {
            setMatched([...matched, firstMatched.name]);
        }

        if (openedCard.length === 2) setTimeout(() => setOpenedCard([]), 1000);
    }, [openedCard]);

    return (
        <div className="App">
            <div className="cards">
                {pairOfImages?.images?.map((pokemon, index) => {
                    //lets flip the card

                    let isFlipped = false;

                    if (openedCard.includes(index)) isFlipped = true;
                    if (matched.includes(pokemon.name)) isFlipped = true;
                    return (
                        <div
                            className={`pokemon-card ${isFlipped ? "flipped" : ""} `}
                            key={index}
                            onClick={() => flipCard(index)}
                        >
                            <div className="inner">
                                <div className="front">
                                    <img
                                        src={pokemon.image}
                                        alt="pokemon-name"
                                        width="100"
                                    />
                                </div>
                                <div className="back"></div>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}




export default MemoryGame


