    return (
        <div className='flex flex-col bg-gray-900' >
            <div className='flex'>
                <PostInputBar />
            </div>

            <div className='flex flex-col align-items-center border-green-100 border-r-2 border-l-2 hover:bg-grey-50 rounded-lg'>

                <Card style={{ width: '30rem' }} >
                    <Card.Img variant="top" className="img-fluid" src="https://images.unsplash.com/photo-1581456495146-65a71b2c8e52?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=772&q=80" />
                    <Card.Body>
                        <div className='flex flex-wrap justify-between text-blue-600 mb-4 mx-3 border-b-2 p-1'>

                            <div className="text-xs flex ">
                                <div>

                                    <img className="text-right w-5 h-5 rounded-full img-fluid" src="https://media-exp1.licdn.com/dms/image/C4D03AQGDf3sUhZgiOQ/profile-displayphoto-shrink_800_800/0/1628360955565?e=1638403200&v=beta&t=4nI5gHqJsfd2Vq8cMdf_jfEBk_QCqTxU6rVV4tqsW8U" alt="" />
