import { initialState } from '../store'



export const usersReducer = (state = initialState.users, action) => {
    switch (action.type) {
        case 'FETCH_USERS':
            return {
                ...state,
                users: action.payload.reverse(),
            }

        case 'ADD_USER':
            return {
                ...state,
                users: [...state.users, action.payload],
            }
        case 'LOG_IN':
            return {
                ...state,
                loggedin: action.payload,
            }


        case 'GET_CURRENT_USER':
            return {
                ...state,
                getcurrentuser: action.payload,
            }

        case 'ADD_LIKES':
            return {
                ...state,
                likes: [...state.likes, action.payload].map(post => post.id === action.payload.id ? post.likes + 1 : post)
            }

        default:
            return state

    }
}
