import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getMusicSearch } from '../../redux/actions'
import { Card, Button } from 'react-bootstrap'
import { Link } from 'react-router-dom'





const MusicList = () => {

    const dispatch = useDispatch()
    const musicList = useSelector(state => state.music.music_lists)
    console.log(musicList[0].data.data)


    useEffect(() => {
        dispatch(getMusicSearch())
    }, [])
    return (
        <div className="flex flex-wrap justify-center pb-5" >
            {
                musicList[0].data.data?.map((music) =>
                (
                    <Link to={`/music/${music.id}/credits`} className="no-underline">
                        <Card key={music.id} className='w-40 h-40 mx-1 my-5  hover:border-purple-800 hover:text-gray-200 border-blue-600 border-b-2 hover:bg-blue-800 rounded-full' >
                            <Card.Img variant="top" className="img-fluid" src={music.artist.picture_medium} />
                            <Card.Body>
                                <Card.Text className='text-center'>
                                    {music.artist.name}
                                </Card.Text>

                            </Card.Body>
                        </Card>
                    </Link>
                )
                )
            }
        </div>
    )
}

export default MusicList
