import axios from 'axios'

export const fetchPosts = () => {
    return async (dispatch, getState) => {
        const posts = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts/`)
        // console.log(posts)
        dispatch({
            type: 'FETCH_POSTS',
            payload: posts
        })

    }
}
