import axios from 'axios'

export const fetchPosts = () => {
    return async (dispatch, getState) => {
        const posts = await axios.post(`${process.env.REACT_APP_BASE_URL}/posts/`)
        console.log(posts)
        dispatch({
            type: 'FETCH_POSTS',
            payload: posts.data
        })

    }
}
