import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import cardImages from '../Games/MemoryGameImages/imagedata.json'
import { selectedCardImage, imageVisibility } from '../../redux/actions'
import { useSelector, useDispatch } from 'react-redux'
import { shuffle } from 'lodash'
import { ImImages } from 'react-icons/im'


const MemoryGame = () => {

    const [allImages, setAllImages] = useState([])
    const [selected, setSelected] = useState([])
    const [selectedImagesArray, setSelectedImagesArray] = useState([])
    const [imageUniqueName, setImageUniqueName] = useState(null)
    const [isVisible, setIsVisible] = useState(false)


    const dispatch = useDispatch()
    const selectedImage = useSelector(state => state.games.selected_card_images)
    const visibleImage = useSelector(state => state.games.visible)



    const handleGameReset = () => {

        setAllImages([])
    }


    const handLoadImage = () => {
        setAllImages(shuffle(cardImages.images))
    }

    useEffect(() => {

    }, [])

    console.log(selected)


    let uniqueImagePairs;
    selected?.length > 0 ? uniqueImagePairs = selected : uniqueImagePairs = []


    const handleVisible = (img, i) => {

        { selected?.includes(img.id) ? setIsVisible(true) : '' }
    }



    const handleImageClick = (img, i) => {
        setSelected([img.id, img.name])
        setSelectedImagesArray([...selectedImagesArray, uniqueImagePairs])

        handleVisible(img, i)
    }



    return (
        <div className="flex flex-col">
            <div className="flex flex-wrap ">
                {
                    allImages?.map((img, i) => (
                        <>

                            <div onClick={() => handleImageClick(img, i)} className={' border rounded-lg border-3 '}>
                                <img className={isVisible ? 'w-20 h-20 opacity-100 ' : 'w-20 h-20 opacity-0 '} src={img.image} alt="" />
                            </div>

                        </>
                    ))
                }

            </div>
            <div className='text-center'>
                {Object.keys(allImages).length === 0
                    ?
                    <button onClick={() => handLoadImage()} className="text-gray-200 bg-indigo-900 text-xl p-2 rounded-lg">START</button>
                    :
                    <button onClick={() => handleGameReset()} className="text-gray-200 bg-red-600 text-xl p-2 rounded-lg">End or Reset</button>
                }
            </div>
        </div>

    )
}

export default MemoryGame
